function risi(x_otrok, y_otrok)    % funkcija sprejme koordinate otroka ob časih t    % nariše krivuljo, po kateri se giblje otrok in animacijo njegovega    % gibanja        hold on;    t = linspace(0, 10, 100);    x = x_otrok(t);    y = y_otrok(t);        % izrisemo krivuljo, po kateri se giblje otrok    plot(x, y, 'k');    % animacija gibanja otroka po krivulji    for i=0:0.1:10        x = x_otrok(i);        y = y_otrok(i);        tocka = plot(x, y, 'b');        pause(0.1);        delete(tocka)    end    hold off;        