function risi_otrok(x_otrok, y_otrok)% RISI_OTROK narise krivuljo, po kateri se giblje otrok %%   VHODNI PODATKI: %       x_otrok, y_otrok: koordinate otroka ob casih t    hold on    t = linspace(0, 10, 100);    x = x_otrok(t);    y = y_otrok(t);        % izrisemo krivuljo, po kateri se giblje otrok    plot(x, y, 'MarkerFaceColor', '#0072BD', 'Linewidth', 1.5);    axis equal;    end        