% parametrizacija krivulje otrokax = @(t) t + cos(t);y = @(t) t + sin(t);% odvoddx = @(t) 1 - sin(t);dy = @(t) 1 + cos(t);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%o = @(t) [x(t); y(t)];do = @(t) [dx(t); dy(t)];% izrisemo krivuljo in animacijo gibanjarisi(x, y);[v, smer] = hitrost_otroka(dx, dy, 5);[t, P] = igraca(o, do);