% x, y: otrok% dx, dy: otrok% o, do: otrok% x_i, y_i: igraca% parametrizacija krivulje otroka (podatki)x = @(t) t + cos(t);y = @(t) t + sin(t);dx = @(t) 1 - sin(t);dy = @(t) 1 + cos(t);% parametrizacija otroka v vektorjuo = @(t) [x(t); y(t)];do = @(t) [dx(t); dy(t)];%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% izracun koordinat igrace[v, smer] = hitrost_otroka(dx, dy, 5);[t, P] = igraca(o, do);% izlocimo x in y koordinate iz matrike P, tj. koordinate igracex_i = P(:, 1);y_i = P(:, 2);% izrisemo krivuljo gibanja otrokahold onrisi_otrok(x, y);% izrisemo krivuljo gibanja igracehold onrisi_igraca(x_i, y_i);% izrisemo animacijo gibanja otroka in igracehold on animacija(x, y, x_i, y_i, t);